import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("User login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def token = holder.accessToken

//Setting a varible in project level with the value
context.testCase.TestSuite.project.setPropertyValue("AI","Bearer "+token)

import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("Get all users").getPropertyValue("response")

def holder = new JsonSlurper().parseText(Data)
def num = holder.count.toInteger()
log.info num

for(i=0;i<num;i++)
{
	if(holder.users[i].name == "uhuhfujdudjd@gmail.com")
	{
	  def userid = holder.users[i]._id
	  log.info userid
	  def converted_uid = userid.toString()
	  log.info converted_uid
	  context.testCase.testSuite.project.setPropertyValue("user_id", converted_uid)
	}
}

import groovy.json.JsonSlurper
def thej = context.testCase.getTestStepByName("Get all product").getPropertyValue("response")

def holder = new JsonSlurper().parseText(thej)
def num = holder.count.toInteger()
log.info num

for(i=0;i<num;i++)
{
	if(holder.products[i].name == "Dairy")
	{
	  def pid = holder.products[i]._id
	  log.info pid
	  def converted_pid = pid.toString()
	  log.info converted_pid
	  context.testCase.testSuite.project.setPropertyValue("pid", converted_pid)

	}
}
